package FOR;

public class contadorMagico {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Contagem crescente: ");
		for (int i = 1; i <=10; i++) {
			System.out.println(i);
			}
		
		System.out.println("Contagem decrescente: ");
		for (int i = 10; i >= 1; i--) {
		System.out.println(i);
		
		}
			
		

	}


	}


